using Microsoft.Maui.Handlers;using Microsoft.Maui;namespace CsvProcessor;public class FileDropViewHandler : ViewHandler<View, FileDropView>{    public static IPropertyMapper<View, FileDropViewHandler> Mapper =        new PropertyMapper<View, FileDropViewHandler>(ViewHandler.ViewMapper);    public FileDropViewHandler()        : base(Mapper)    {    }    protected override FileDropView CreatePlatformView()    {        return new FileDropView();    }}